mapper.select:
select a.*, (SELECT MAX(b.cc_no) FROM jc_match b WHERE a.comp_id=b.comp_id) AS maxCcNo
from jc_match_dict a
where comp_id = #{compId,jdbcType=VARCHAR}

mapper.insert:
insert into jc_match_dict (comp_id, comp_name, match_type,
  create_time, update_time, oper_id
  )
values (#{compId,jdbcType=VARCHAR}, #{compName,jdbcType=VARCHAR}, #{matchType,jdbcType=DECIMAL}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{operId,jdbcType=VARCHAR}
  )
